import { inject, injectable } from 'inversify';
import { IDocumentUpdate, IObject } from '../common/commonInterface';
import { ERROR } from '../constants/errorMessage';
import { IDENTIFIER } from '../constants/identifier';
import { PASSION_LEVEL } from '../constants/passionLevel';
import { IHobby, IHobbyDocument } from '../models/hobbiesModel';
import { IHobbyRepository } from '../repository/hobbyRepository';
import { NotFoundError } from '../utils/errorUtil';
import { IUserService } from './userService';

export interface IHobbySearchQuery {
  user: string,
  page: number,
  limit: number
}

export interface IUpdateHobby {
  name?: string,
  passion_level?: PASSION_LEVEL,
  from?: number,
  is_deleted?: boolean
}

export interface IHobbyService {
  addHobby(hobbyData: IHobby): Promise<IHobbyDocument>;
  updateHobby(query: IObject, updateData: IUpdateHobby): Promise<IDocumentUpdate>;
}

@injectable()
export class HobbyService implements IHobbyService {

  private hobbyRepository;
  private userService;
  constructor(
    @inject(IDENTIFIER.HobbyRepository) hobbyRepository: IHobbyRepository,
    @inject(IDENTIFIER.UserService) userService: IUserService,
  ) {
    this.hobbyRepository = hobbyRepository;
    this.userService = userService
  }

  public async addHobby(hobbyData: IHobby): Promise<IHobbyDocument> {
    let user = await this.userService.getUserById(hobbyData.user);
    if (!user) {
      throw new NotFoundError(ERROR.USER_DOES_NOT_EXISTS);
    }
    return this.hobbyRepository.createHobby(hobbyData);
  }

  public async updateHobby(query: IObject, updateData: IUpdateHobby): Promise<IDocumentUpdate> {
    const hobby = await this.hobbyRepository.getHobbyById(query._id);
    if (!hobby) {
      throw new NotFoundError(ERROR.HOBBY_NOT_FOUND)
    }
    return this.hobbyRepository.updateHobby(query, updateData); 
  }

}

import { injectable } from 'inversify';
import { hobbyModel, IHobby, IHobbyDocument } from '../models/hobbiesModel';
import { IObject, IPagination, ISearchQuery, IDocumentUpdate } from '../common/commonInterface';
import { IUpdateHobby } from '../services/hobbyService';
import { ObjectId } from 'bson';

export interface IHobbyRepository {
  createHobby(hobbyData: IHobby): Promise<IHobbyDocument>;
  updateHobby(query: IObject, updateData: IUpdateHobby): Promise<IDocumentUpdate>;
  getHobbyById(hobbyId: ObjectId, project?: {}): Promise<IHobbyDocument> 
}

@injectable()
export class HobbyRepository implements IHobbyRepository {

  public async createHobby(hobbyData: IHobby): Promise<IHobbyDocument> {
    return hobbyModel.create(hobbyData);
  }

  public async getHobbyById(hobbyId: ObjectId, project?: {}): Promise<IHobbyDocument> {
    return hobbyModel
      .findById({ _id: hobbyId }, project)
      .lean();
  }

  public async getHobbies(searchQuery: ISearchQuery): Promise<IPagination<IHobbyDocument>> {
    if (!searchQuery.baseQuery) throw new Error('BASE Query does not exist');
    const data = await hobbyModel
    .find(searchQuery.baseQuery)
    .select(searchQuery.filterQuery?.select)
    .skip(searchQuery.filterQuery?.skip)
    .limit(searchQuery.filterQuery?.limit)
    .lean(searchQuery.filterQuery?.lean || false);

    let totalRecords;
    if (searchQuery.totalCountQuery) {
      totalRecords = await hobbyModel.count(searchQuery);
    }

    return { 
      data, 
      totalRecords,
      recordsPerPage:  searchQuery.filterQuery?.limit
    };
  }

  public async updateHobby(query: IObject, updateData: IUpdateHobby): Promise<IDocumentUpdate> {
    return hobbyModel.updateOne(query, { $set: updateData });
  }
}

import { ObjectId } from 'bson';
import { Model, model, Schema } from 'mongoose';
import { PASSION_LEVEL } from '../constants/passionLevel';

export interface IHobbyDocument extends IHobby {
  _id: ObjectId,
  created_at: Date,
  updated_at: Date
}

export interface IHobby {
  name:  string,
  passion_level: PASSION_LEVEL,
  user: ObjectId,
  from: number,
  is_deleted: boolean
}

export const hobbySchema = new Schema(
  {
    name: { type: String, required: true },
    passion_level: { type: String, required: true, enum: PASSION_LEVEL },
    user: { type: Schema.Types.ObjectId, required: true, ref: 'user' }, // Principle of Least Cardinality - Store relationships in a way that minimizes the size of individual documents.
    from: { type: Number, required: true },
    is_deleted: { type: Boolean, default: false }
  },
  { timestamps: { createdAt: 'created_at', updatedAt: 'updated_at' } }
);

export const hobbyModel: Model<IHobbyDocument> = model<IHobbyDocument>('hobbies', hobbySchema);

export enum PASSION_LEVEL {
  LOW,
  MEDIUM,
  HIGH,
  VERY_HIGH
}

class BaseError extends Error {    
  constructor(description: string) {
    super(description);
    Object.setPrototypeOf(this, new.target.prototype);
    Error.captureStackTrace(this);
  }
}

export class InternalServerError extends BaseError {
  constructor (description) {
    super(description);
    this.name = 'Internal Server Error';
  }
}

export class BadRequestError extends BaseError {
  constructor (description) {
    super(description);
    this.name = 'Bad Request';
  }
}

export class DuplicateError extends BaseError {
  constructor(description: string) {
    super(description);
    this.name = 'Duplicate Error';
  }
}

export class AuthorizationError extends BaseError {
  constructor(description: string) {
    super(description);
    this.name = 'Authorization Error';
  }
}

export class NotFoundError extends BaseError {
  constructor(description: string) {
    super(description);
    this.name = 'Not Found Error';
  }
}

